package contract

import (
	"math/big"
	"strings"

	"github.com/seeleteam/go-seele/common"
	"github.com/seeleteam/go-seele/common/hexutil"
	"github.com/seeleteam/labs/abi"
)

// HashedTimelockABI is the input ABI used to generate the binding from.
const HashedTimelockABI = "[{\"constant\":false,\"inputs\":[{\"name\":\"_receiver\",\"type\":\"address\"},{\"name\":\"_hashlock\",\"type\":\"bytes32\"},{\"name\":\"_timelock\",\"type\":\"uint256\"}],\"name\":\"newContract\",\"outputs\":[{\"name\":\"contractId\",\"type\":\"bytes32\"}],\"payable\":true,\"stateMutability\":\"payable\",\"type\":\"function\"},{\"constant\":false,\"inputs\":[{\"name\":\"_contractId\",\"type\":\"bytes32\"},{\"name\":\"_preimage\",\"type\":\"bytes32\"}],\"name\":\"withdraw\",\"outputs\":[{\"name\":\"\",\"type\":\"bool\"}],\"payable\":false,\"stateMutability\":\"nonpayable\",\"type\":\"function\"},{\"constant\":false,\"inputs\":[{\"name\":\"_contractId\",\"type\":\"bytes32\"}],\"name\":\"refund\",\"outputs\":[{\"name\":\"\",\"type\":\"bool\"}],\"payable\":false,\"stateMutability\":\"nonpayable\",\"type\":\"function\"},{\"constant\":true,\"inputs\":[{\"name\":\"_contractId\",\"type\":\"bytes32\"}],\"name\":\"getContract\",\"outputs\":[{\"name\":\"sender\",\"type\":\"address\"},{\"name\":\"receiver\",\"type\":\"address\"},{\"name\":\"amount\",\"type\":\"uint256\"},{\"name\":\"hashlock\",\"type\":\"bytes32\"},{\"name\":\"timelock\",\"type\":\"uint256\"},{\"name\":\"withdrawn\",\"type\":\"bool\"},{\"name\":\"refunded\",\"type\":\"bool\"},{\"name\":\"preimage\",\"type\":\"bytes32\"}],\"payable\":false,\"stateMutability\":\"view\",\"type\":\"function\"},{\"anonymous\":false,\"inputs\":[{\"indexed\":true,\"name\":\"contractId\",\"type\":\"bytes32\"},{\"indexed\":true,\"name\":\"sender\",\"type\":\"address\"},{\"indexed\":true,\"name\":\"receiver\",\"type\":\"address\"},{\"indexed\":false,\"name\":\"amount\",\"type\":\"uint256\"},{\"indexed\":false,\"name\":\"hashlock\",\"type\":\"bytes32\"},{\"indexed\":false,\"name\":\"timelock\",\"type\":\"uint256\"}],\"name\":\"LogHTLCNew\",\"type\":\"event\"},{\"anonymous\":false,\"inputs\":[{\"indexed\":true,\"name\":\"contractId\",\"type\":\"bytes32\"}],\"name\":\"LogHTLCWithdraw\",\"type\":\"event\"},{\"anonymous\":false,\"inputs\":[{\"indexed\":true,\"name\":\"contractId\",\"type\":\"bytes32\"}],\"name\":\"LogHTLCRefund\",\"type\":\"event\"}]"

// HashedTimelockBin is the compiled bytecode used for deploying new contracts.
const HashedTimelockBin = ``

// SeeleContract is a type of seelecontract to parse the function
type SeeleContract struct {
	Address common.Address // Deployment address of the contract on the seele blockchain
	Abi     abi.ABI        // Reflect based ABI to access the correct Ethereum methods
}

// NewSeeleContract Create a seelecontract
func NewSeeleContract(address common.Address) (*SeeleContract, error) {
	parsed, err := abi.JSON(strings.NewReader(HashedTimelockABI))
	if err != nil {
		return nil, err
	}

	return &SeeleContract{
		Address: address,
		Abi:     parsed,
	}, nil
}

// GetContractByteCode get the contract bytecode
func GetContractByteCode() ([]byte, error) {
	bytecode, err := hexutil.HexToBytes(HashedTimelockBin)
	if err != nil {
		return nil, err
	}

	return bytecode, err
}

// GetContract return the GetContract function bytecode
func (seele *SeeleContract) GetContract(_contractId [32]byte) ([]byte, error) {
	return seele.getFuncByteCode("getContract", _contractId)
}

// NewContract return the NewContract function bytecode
func (seele *SeeleContract) NewContract(_receiver common.Address, _hashlock [32]byte, _timelock *big.Int) ([]byte, error) {
	return seele.getFuncByteCode("newContract", _receiver, _hashlock, _timelock)
}

// Refund return the Refund function bytecode
func (seele *SeeleContract) Refund(_contractId [32]byte) ([]byte, error) {
	return seele.getFuncByteCode("refund", _contractId)
}

// Withdraw return the Withdraw function bytecode
func (seele *SeeleContract) Withdraw(_contractId [32]byte, _preimage [32]byte) ([]byte, error) {
	return seele.getFuncByteCode("withdraw", _contractId, _preimage)
}

// GetContractInfo returns tht contract info
func (seele *SeeleContract) GetContractInfo(_contractId [32]byte) ([]byte, error) {
	return seele.getFuncByteCode("getContract", _contractId)
}

func (seele *SeeleContract) getFuncByteCode(method string, params ...interface{}) ([]byte, error) {
	return seele.Abi.Pack(method, params...)
}
